var teoria  = require('teoria')
  , piu     = require('./index');
    
// Infer the chords (strictly) constituted by the notes D, F, A, C
console.log("********************************************** 1 ************************************************");
    
var result1 = piu.infer( ['d', 'f', 'a', 'c'].map(teoria.note) ).map( piu.name );
console.log("typeOf(result2)=" + typeof(result1) + " result1.length=" + result1.length);
console.log("piu.infer( ['d', 'f', 'a', 'c'].map(teoria.note) ).map( piu.name )=" + result1);
    
console.log("********************************************** 2 ************************************************");
    
// Infer the chords (enharmonically) constituted by the notes B, D, F and Ab
var result2 = piu.infer( ['b', 'd', 'f', 'ab'].map(teoria.note) ).map( piu.name );
console.log("typeOf(result2)=" + typeof(result2) + " result2.length=" + result2.length);
console.log("piu.infer( ['b', 'd', 'f', 'ab'].map(teoria.note) ).map( piu.name )=" + result2);
